{
    "name": "Lead Follow-up",
    "description": "Template for following up with leads",
    "prompt": "Generate a follow-up email for {{lead_name}}",
    "category": "email"
}

{
    "message": "Can you analyze this lead and suggest next steps?"
}

{
    "type": "lead_assigned",
    "channel": "email",
    "subject": "New lead assigned to you",
    "body": "You have been assigned lead: {{lead_name}}"
}

{
    "site_id": 1,
    "name": "Jane Doe",
    "position": "Marketing Manager",
    "bio": "Jane has 10 years of experience in digital marketing",
    "photo": "https://example.com/jane.jpg",
    "email": "jane@example.com",
    "social_links": {
        "linkedin": "https://linkedin.com/in/janedoe"
    },
    "status": "published"
}

{
    "site_id": 1,
    "name": "Contact Form",
    "description": "Main contact form",
    "fields": [
        {
            "name": "name",
            "type": "text",
            "label": "Name",
            "required": true
        },
        {
            "name": "email",
            "type": "email",
            "label": "Email",
            "required": true
        }
    ],
    "is_active": true
}

{
    "name": "Monthly Sales Report",
    "description": "Sales performance for the month",
    "type": "sales",
    "filters": {
        "start_date": "2026-01-01",
        "end_date": "2026-01-31",
        "pipeline_id": 1
    },
    "columns": ["lead_name", "value", "status", "owner"]
}
